INSERT INTO film (title, category, production_year, details, price)
VALUES
    ('Inception', 'Sci-Fi', 2010, 'Dominic Cobb to specjalista od ekstrakcji - kradnie myśli podczas snu. Gdy otrzymuje zadanie wszczepienia idei, musi zmierzyć się z własnymi demonami przeszłości. Film eksploruje poziomy świadomości, gdzie czas płynie inaczej, a granica między rzeczywistością a snem staje się niewyraźna. Widowiskowe efekty specjalne i złożona narracja.', 29.99),
    ('The Shawshank Redemption', 'Dramat', 1994, 'Andy Dufresne, bankier skazany za morderstwo żony, trafia do więzienia Shawshank. Pomimo brutalnej rzeczywistości, zachowuje godność i nadzieję. Jego przyjaźń z Redem i systematyczne dążenie do wolności tworzą poruszającą opowieść o ludzkiej duchowości. Film oparty na opowiadaniu Stephena Kinga.', 22.50),
    ('Pulp Fiction', 'Kryminał', 1994, 'Nielinearna opowieść o gangsterach, bokserze i parze rabusiów w Los Angeles. Kultowe dialogi, brutalność i czarny humor przeplatają się w historii pełnej zwrotów akcji. Vincent Vega i Jules Winnfield wykonują brudną robotę dla Marsellusa Wallace''a, podczas Butch walczy o przetrwanie.', 24.99),
    ('The Dark Knight', 'Akcja', 2008, 'Batman stawia czoła Jokerowi - anarchistycznemu przestępcy, który chce pogrążyć Gotham w chaosie. Christopher Nolan reinterpretuje mit Człowieka-Nietoperza, tworząc mroczny thriller moralny. Heath Ledger w przełomowej roli Jokera pokazuje szaleństwo bez celu. Walka o duszę miasta.', 27.99),
    ('Titanic', 'Romans', 1997, 'Różnorodność klas społecznych na pokładzie statku zderza się gdy młoda arystokratka Rose zakochuje się w biednym artyście Jacku. Ich miłość kwitnie podczas rejsu, by zostać wystawiona na próbę przez katastrofę. Epicka historia z wiernie odtworzonym zatonięciem Titanica.', 18.99),
    ('Jurassic Park', 'Przygodowy', 1993, 'Grupa naukowców zostaje zaproszona do parku rozrywki z klonowanymi dinozaurami. Gdy systemy bezpieczeństwa zawodzą, goście muszą walczyć o przetrwanie. Film pionierski pod względem efektów CGI, ukazujący ludzką arogancję wobec sił natury. "Life finds a way" - słynne słowa dr. Malcolma.', 21.99),
    ('Avatar', 'Fantasy', 2009, 'Jake Sully, sparaliżowany marine, uczestniczy w programie awatarów na planecie Pandora. Wcielając się w ciało Na''vi, poznaje ich kulturę i zakochuje się w Neytiri. Konflikt między chciwością ludzi a duchowością rdzennych mieszkańców. Rewolucyjna technologia motion capture.', 26.50),
    ('Fight Club', 'Dramat', 1999, 'Narrator, cierpiący na bezsenność, poznaje tajemniczego mydlarza Tylera Durden. Razem zakładają podziemny klub walki, który ewoluuje w coś znacznie groźniejszego. Ostra krytyka konsumpcjonizmu i męskiej tożsamości. Szokujące zwroty akcji i kultowe cytaty. "Pierwsza zasada Fight Clubu..."', 23.75),
    ('The Godfather', 'Gangsterski', 1972, 'Opowieść o rodzinie mafijnej Corleone. Don Vito przekazuje władzę najmłodszemu synowi Michaełowi, który stopniowo zatraca resztki moralności. Studium władzy, lojalności i amerykańskiego snu. Marlon Brando i Al Pacino w ikonicznych rolach. "Oferty nie można odrzucić".', 28.99),
    ('Interstellar', 'Sci-Fi', 2014, 'W przyszłości Ziemia umiera. Grupa astronautów wyrusza przez tunel czasoprzestrzenny w poszukiwaniu nowego domu. Cooper musi wybrać między misją a rodziną. Naukowo wiarygodne przedstawienie czarnych dziur i dylatacji czasu. Wzruszająca relacja ojca i córki rozdzielonych przez wszechświat.', 30.50),
    ('The Lord of the Rings: The Fellowship of the Ring', 'Fantasy', 2001, 'Hobbit Frodo otrzymuje Pierścień Władzy i wyrusza w podróż do Mordoru, by go zniszczyć. Towarzyszy mu drużyna reprezentująca różne rasy Śródziemia. Epicka adaptacja Tolkiena z zapierającymi dech krajobrazami Nowej Zelandii. Początek trylogii zmieniającej kino.', 31.99),
    ('Gladiator', 'Historyczny', 2000, 'Generał Maximus zostaje zdradzony, gdy cesarz Marek Aureliusz umiera. W niewoli jako gladiator, dąży do zemsty na nowym cesarzu Kommodusie. Spektakularne walki na arenie i poruszająca historia utraconej rodziny. "Czy nie jesteś rozentuzjazmowany? Czy nie jesteś podekscytowany?"', 25.25),
    ('The Silence of the Lambs', 'Thriller', 1991, 'Młoda agentka Clarice Starling konsultuje się z uwięzionym kanibalem Hannibalem Lecterem, by schwytać seryjnego mordercę Buffalo Billa. Psychologiczna gra kotka i myszki. Jodie Foster i Anthony Hopkins w niezapomnianych rolach. Jedyny horror z Oscarem dla najlepszego filmu.', 20.99),
    ('Schindler''s List', 'Historyczny', 1993, 'Oskar Schindler, niemiecki przedsiębiorca, ratuje Żydów podczas Holocaustu zatrudniając ich w fabryce. Czarno-białe zdjęcia podkreślają realizm tej poruszającej historii. Liam Neeson w roli człowieka, którego sumienie budzi się wobec okrucieństw wojny. "Kto ratuje jedno życie..."', 19.50),
    ('The Green Mile', 'Dramat', 1999, 'Strażnik więzienny Paul Edgecombe poznaję niezwykłego skazańca Johna Coffeya, obdarzonego nadprzyrodzonymi zdolnościami. Wzruszająca opowieść o cierpieniu, odkupieniu i cudach na oddziale śmierci. Adaptacja powieści Stephena Kinga z Tomem Hanksem w roli głównej.', 22.75),
    ('The Pianist', 'Wojenny', 2002, 'Prawdziwa historia Władysława Szpilmana, żydowskiego pianisty walczącego o przetrwanie w okupowanej Warszawie. Adrien Brody w oscarowej roli pokazuje samotność człowieka wśród ruin getta. Reżyseria Romana Polańskiego - osobiste spojrzenie na temat wojny.', 21.25),
    ('The Departed', 'Gangsterski', 2006, 'Policjant pod przykrywką w mafii i mafioso infiltrujący policję. Napięta gra tożsamości w bostońskim półświatku. Leonardo DiCaprio i Matt Damon w rolach antagonistów. Martin Scorsese w szczytowej formie. Remake hongkońskiego "Infernal Affairs".', 24.50),
    ('The Prestige', 'Thriller', 2006, 'Rywalizacja dwóch magików w wiktoriańskiej Anglii przybiera mroczny obrót. Christopher Nolan bada temat obsesji i poświęcenia dla sztuki. "Czy obserwowałeś uważnie?" - film pełen zwrotów akcji i iluzji. Hugh Jackman i Christian Bale w głównych rolach.', 26.99),
    ('Django Unchained', 'Western', 2012, 'Niewolnik Django zostaje uwolniony przez łowcę nagród, by odnaleźć żonę. Quentin Tarantino reinterpretuje western przez pryzmat niewolnictwa. Christoph Waltz jako dr King Schultz i Leonardo DiCaprio jako sadystyczny Calvin Candie. Kontrowersyjna, ale ważna opowieść o zemście.', 27.50),
    ('The Social Network', 'Dramat', 2010, 'Powstanie Facebooka jako historia przyjaźni, zdrady i geniuszu. Jesse Eisenberg jako Mark Zuckerberg w procesie sądowym o prawa do serwisu. Szybkie dialogi Aarona Sorkina i reżyseria Davida Finchera. "Miljarderze w pidżamie" - ikoniczna scena programowania.', 23.99),
    ('La La Land', 'Musical', 2016, 'Mia, aspirująca aktorka, i Sebastian, jazzowy purysta, zakochują się w Los Angeles. Współczesny hołd dla klasycznych musicali z melancholijnym zakończeniem. Emma Stone i Ryan Gosling w pełnych wdzięku rolach. Nagrodzone Oscarem piosenki i choreografia.', 25.75),
    ('Parasite', 'Thriller', 2019, 'Biedna rodzina infiltruje dom bogaczy, ale sytuacja wymyka się spod kontroli. Bong Joon-ho łączy czarną komedię, thriller i komentarz społeczny. Pierwszy nieanglojęzyczny film z Oscarem dla najlepszego filmu. "Najlepszy plan to brak planu" - ironia losu.', 28.25),
    ('Whiplash', 'Dramat', 2014, 'Młody perkusista Andrew staje przed tyrańskim nauczycielem Terence''a Fletchera. Jak daleko można się posunąć dla wielkości? Intensywne sceny muzyczne i psychologiczna walka. JK Simmons w oscarowej roli mentora przekraczającego granice. "Nie za szybko, nie za wolno."', 22.99),
    ('Mad Max: Fury Road', 'Akcja', 2015, 'W postapokaliptycznej pustyni Max i Furiosa uciekają tyranowi Immortanowi Joe. Dwugodzinny pościg z minimalnymi dialogami. George Miller redefiniuje kino akcji praktycznymi efektami. "Witness me!" - kultowy wizerunek Wojowników Śmierci. Feministyczny wymiar w świecie chaosu.', 26.25),
    ('Her', 'Sci-Fi', 2013, 'Samotny pisarz Theodore zakochuje się w systemie operacyjnym Samantha. Delikatna opowieść o miłości w erze cyfrowej. Joaquin Phoenix i głos Scarlett Johansson tworzą niezwykłą chemię. Wizja przyszłości pełnej technologii, ale i ludzkiej tęsknoty.', 24.75);
